func TestNewApi(t *testing.T) {
	config := ReadConfig("../../test/")
	config.Init()
	serverStarted := new(sync.WaitGroup)
	serverStarted.Add(1)
	api := NewApi(config.Api)
	go api.Handle(config.Server, serverStarted)
	//Waiting http server to start
	serverStarted.Wait()
}

